// in src/App.js
import React from 'react';
import {
                Admin,
                fetchUtils,
                Resource,
 } from 'react-admin';
import jsonServerProvider from 'ra-data-json-server';
import authProvider from './authProvider';


const httpClient = (url, options = {}) => {
        if (!options.headers) {
                options.headers = new Headers({ Accept: 'application/json' });
        }
        const authString = localStorage.getItem('authString');
        options.headers.set('Authorization', `Basic ${authString}`);
        return fetchUtils.fetchJson(url, options);
}
const dataProvider = jsonServerProvider('http://3.83.252.232:3000',httpClient);
const App = () => (
        <Admin authProvider={authProvider} dataProvider={dataProvider}>
                <Resource name="Students" list={StudentList} show={StudentShow} create={StudentCreate} edit={StudentEdit}/>
                <Resource name="Grades" list={GradeList} show={GradeShow} create={GradeCreate} edit={GradeEdit}/>
        </Admin>
);
export default App;
